import React, { useState } from "react";
import { Component } from "react";
import { Button, FormGroup, FormControl } from "react-bootstrap";
import "./Login.css";

class Login extends Component {

    constructor(props) {
        super(props);

        this.state = {
            email: "7788758095@c.goopter.com",
            password: "20200506",
        };



    }

    handleSubmit(event) {

        let loginInfo = {
            "data": {
                "email": this.state.email,
                "password": this.state.password
            }
        }

        const requestOptions = {
            method: 'POST',
            headers: {
                'Accept': 'application/json',
                'Content-Type': 'application/json',
            },
            body: JSON.stringify(loginInfo)
        };

        fetch('https://api-qa.goopter.com/api/rest/v8/login', requestOptions)
            .then(res => res.json())
            .then(
                (result) => {
                    console.log(result)
                    //this.navigatePage(result.records.token,result.records.secret)
                    window.location = '/' + result.records.token + '+' +  result.records.secret
                },
                (error) => {
                    console.log(error)
                }
            )

        event.preventDefault();

    }

    /*
    navigatePage(resultToken, resultSecret) {
        React.useEffect(() => {
            localStorage.setItem('tokens', JSON.stringify(resultToken + '+' + resultSecret))
        })
        window.location = '/' //+ result.records.token + '+' +  result.records.secret
    }
*/
    handleChange(event) {
        const target = event.target;
        const value = target.type === 'checkbox' ? target.checked : target.value;
        const name = target.name;
        this.setState({
            [name]: value
        });
    }

    render() {
        return (
            <div className="Login">
                <form onSubmit={this.handleSubmit}>
                    <FormGroup controlId="email" bsSize="large">
                        <label>Email</label>
                        <FormControl
                            autoFocus
                            name = "email"
                            type="email"
                            value={this.state.email}
                            onChange={this.handleChange}
                        />
                    </FormGroup>
                    <FormGroup controlId="password" bsSize="large">
                        Password
                <FormControl
                name = "password"
                            value={this.state.password}
                            onChange={this.handleChange}
                            type="password"
                        />
                    </FormGroup>
                    <Button block bsSize="large" type="submit">
                        Login
                </Button>
                </form>
            </div>
        );
    }
}


export default Login;
