import React, { useState } from "react";
import { Button, FormGroup, FormControl } from "react-bootstrap";
import "./Login.css";
import 'bootstrap/dist/css/bootstrap.min.css'

export default function Login() {

    //Default input for testing purposes
    const [email, setEmail] = useState("7788758095@c.goopter.com");
    const [password, setPassword] = useState("20200506");

    function validateForm() {
        return email.length >= 0 && password.length >= 0;
    }

    function handleSubmit(event) {
        let loginInfo = {
            "data": {
                "email": email,
                "password": password
            }
        }

        const requestOptions = {
            method: 'POST',
            headers: {
                'Accept': 'application/json',
                'Content-Type': 'application/json',
            },
            body: JSON.stringify(loginInfo)
        };

        fetch('https://api-qa.goopter.com/api/rest/v8/login', requestOptions)
            .then(res => res.json())
            .then(
                (result) => {
                    console.log(result)
                    window.location = '/' + result.records.token + '+' + result.records.secret
                },
                (error) => {
                    console.log(error)
                }
            )
        event.preventDefault();
    }

    return (
        <div className="Login row">
            <form onSubmit={handleSubmit}>
                <FormGroup controlId="email" bsSize="large">
                    <label>Email</label>
                    <FormControl
                        autoFocus
                        type="email"
                        value={email}
                        onChange={e => setEmail(e.target.value)}
                    />
                </FormGroup>
                <FormGroup controlId="password" bsSize="large">
                    Password
                <FormControl
                        value={password}
                        onChange={e => setPassword(e.target.value)}
                        type="password"
                    />
                </FormGroup>
                <Button block bsSize="large" disabled={!validateForm()} type="submit">
                    Login
                </Button>
            </form>
        </div>
    );
}
