import React, { useState } from "react";
import { Component } from "react";
import CryptoJS from 'crypto-js/';
import $ from 'jquery';
import { Button, FormGroup, FormControl } from "react-bootstrap";

const crypto = require('crypto')
const OAuth = require('oauth-1.0a')

class UserPage extends Component {
    constructor(props) {
        super(props);

        console.log(window.location.pathname)
        if (window.location.pathname == "/") {

            //Trigger redirect info
            //Throw some conditional rendering here
            //timer that goes 3,2,1....
            window.location = '/login'
        }

        this.state = {
            nickname: "",
            firstname: ""
        };

    }

    componentDidMount(event) {

        const oauth = OAuth({
            consumer: {
                key: '8fb7ec71f8b4e1f2ec28d2f8c3f7785a',
                secret: 'af035f0f340e090d5b51870f9a168acd',
            },
            signature_method: 'HMAC-SHA1',
            hash_function(base_string, key) {
                return CryptoJS.HmacSHA1(base_string, key).toString(CryptoJS.enc.Base64)
            },
        })

        const request_data = {
            url: 'https://api-qa.goopter.com/api/rest/v7/customerinfo',
            method: 'GET',
        }

        //Replace with ReDux state later
        let token_key = window.location.pathname.substring(1, 33)
        let token_secret = window.location.pathname.substring(34)

        const token = {
            key: token_key,
            secret: token_secret,
        }

        $.ajax({
            url: request_data.url,
            type: request_data.method,
            headers: oauth.toHeader(oauth.authorize(request_data, token)),
            success: function (data) {
                this.setState({
                    nickname: data.records.nick_name.toString(),
                    firstname: data.records.first_name.toString()
                })
            }.bind(this)
        })

    }

    changeNickName(event) {

        event.preventDefault();

        const oauth = OAuth({
            consumer: {
                key: '8fb7ec71f8b4e1f2ec28d2f8c3f7785a',
                secret: 'af035f0f340e090d5b51870f9a168acd',
            },
            signature_method: 'HMAC-SHA1',
            hash_function(base_string, key) {
                return CryptoJS.HmacSHA1(base_string, key).toString(CryptoJS.enc.Base64)
            },
        })

        const request_data = {
            url: 'https://api-qa.goopter.com/api/rest/v7/customerinfo',
            method: 'POST',
            data: {
                first_name: 'DocCool',
                nick_name: "QueenfCool"
            },
        }

        //Replace with ReDux state later
        let token_key = window.location.pathname.substring(1, 33)
        let token_secret = window.location.pathname.substring(34)

        const token = {
            key: token_key,
            secret: token_secret,
        }

        console.log(token_key)
        console.log(token_secret)

        $.ajax({
            url: request_data.url,
            type: request_data.method,
            data: request_data.data,
            headers: oauth.toHeader(oauth.authorize(request_data, token)),
            success: function (data) {
                console.log(data)
            }.bind(this)
        })
    }


    render() {
        return (
            <div>
                Nick Name : {this.state.nickname}
                <br></br>
                First Name : {this.state.firstname}

                <form onSubmit={this.changeNickName}>
                    <Button block bsSize="large" type="submit">
                        Update Names
                </Button>
                </form>
            </div>
        )
    }

}

export default UserPage;
